    // فتح ملف صورة واعادة بت ماب مع امكانية تغيير حجم الصورة
    /// <summary>
    /// 07002  >>  open image fill then return bitmap file and we can Change Size 
    /// </summary>
    /// <param name="Result">Return : Result Of Operation /string[2] Variable</param>
    /// <param name="ReturnBitmap">Required ref Image</param>
    /// <param name="FilePath">Required Image Path </param>
    /// <param name="NewWidth">Optional Image Width</param>
    /// <param name="NewHeight">Optional Image Height</param>
    /// <returns> image as a Bitmap Variable and a result as a string Variable</returns>
    public static void OpenImageFileThenReturnBitmapFile(ref string[] Result, ref Bitmap ReturnBitmap, string FilePath, int NewWidth = 0, int NewHeight = 0)
    {
        Result = new string[2];
        Result[0] = "01001001";
        try
        {
            if (File.Exists(FilePath))
            {
                if (NewWidth != 0 && NewHeight != 0)
                {
                    // change size
                    FileStream fs = File.Open(FilePath, FileMode.Open);
                    Size s1 = new Size(NewWidth, NewHeight);
                    Bitmap bmp = new Bitmap(Image.FromStream(fs), s1);
                    fs.Close();
                    ReturnBitmap = bmp;
                    Result[1] = "success";
                }
                else if (NewWidth == 0 && NewHeight == 0)
                {
                    // don not change size
                    FileStream fs = File.Open(FilePath, FileMode.Open);
                    Bitmap bmp = new Bitmap(Image.FromStream(fs));
                    fs.Close();
                    ReturnBitmap = bmp;
                    Result[1] = "success";
                }
                else
                {
                    ReturnBitmap = null;
                    Result[1] = "The operation was not successful  !!! \n '01001001' >> Width or height equals zero  !!!";
                }
            }
            else
            {
                ReturnBitmap = null;
                Result[1] = "The operation was not successful  !!! \n '01001001' >> the file not exists  !!! ";
            }
        }
        catch (Exception ex)
        {
            GeneralAction.AddNewNotification("Images >> OpenImageFileThenReturnBitmapFile ", DateTime.Now, ex.Message, ex.Message);
       
            ReturnBitmap = null;
            Result[1] = "The operation was not successful  !!! \n  you have proplem in 0001 methode   !!!  " + ex.Message;
        }

        /*
        string s;
        Bitmap mb;
        //MyTools.Images.OpenImageFileThenReturnBitmapFile_0001(@"C:\Users\user\Desktop\Capture.PNG",ref mb,ref s,1000,1000);  // change size
