    // تغيير حجم الصورة
    /// <summary>
    /// 07002  >>  changeImageSise
    /// </summary>
    /// <param name="ReturnBitmap">Required ref Image</param>
    /// <param name="NewWidth">Optional Image Width</param>
    /// <param name="NewHeight">Optional Image Height</param>
    /// <returns> Image Variable</returns>
    public static Image changeImageSise(Image img, int NewWidth = 0, int NewHeight = 0)
    {
        try
        {
            Size size = new Size(NewWidth, NewHeight);
            return (Image)(new Bitmap(img, size));
        }
        catch (Exception ex)
        {
            GeneralAction.AddNewNotification("Images >> changeImageSise ", DateTime.Now,ex.Message ,ex.Message );
            return img;
        }

    }



    // فتح ملف صورة واعادة بت ماب مع امكانية تغيير حجم الصورة
    /// <summary>
    /// 07002  >>  Open Image File on the pc Then Return Image Vareable With Cheange Size And image Type
    /// </summary>
    /// <param name="FilePath">Required Image Path </param>
    /// /// <param name="mageFormat">Required System.Drawing.Imaging.ImageFormat. png or ... </param>
    /// <param name="NewWidth">Optional Image Width</param>
    /// <param name="NewHeight">Optional Image Height</param>
    /// <returns> image as a Bitmap Variable and a result as a string Variable</returns>
    public static Image OpenImageFileThenReturnImageVareableWithCheangeSizeAndType(  string FilePath,  System.Drawing.Imaging.ImageFormat  mageFormat , int NewWidth = 0, int NewHeight = 0)
    {

        try
        {
            if (File.Exists(FilePath))
            {
                if (NewWidth != 0 && NewHeight != 0)
                {
                    Size s1 = new Size(NewWidth, NewHeight);
                    Bitmap bmp = new Bitmap(Image.FromFile(FilePath), s1);
                    MemoryStream ms = new MemoryStream();
                    bmp.Save(ms, mageFormat);
                    return Image.FromStream(ms);

                }
                else if (NewWidth == 0 && NewHeight == 0)
                {
                    // don not change size
                    Bitmap bmp = new Bitmap(Image.FromFile(FilePath));
                    MemoryStream ms = new MemoryStream();
                    bmp.Save(ms, mageFormat);
                    return Image.FromStream(ms);

                }
                else
                {
                    return null;
                    GeneralAction.AddNewNotification("Images >> OpenImageFileThenReturnBitmapFile ", DateTime.Now, "The operation was not successful  !!!   '01001001' >> Width or height equals zero  !!!", "The operation was not successful  !!!   '01001001' >> Width or height equals zero  !!!");
                }
            }
            else
            {
                return null;
                GeneralAction.AddNewNotification("Images >> OpenImageFileThenReturnBitmapFile ", DateTime.Now, "The operation was not successful  !!!   '01001001' >> the file not exists  !!! ", "The operation was not successful  !!!   '01001001' >> the file not exists  !!! ");

            }
        }
        catch (Exception ex)
        {
            GeneralAction.AddNewNotification("Images >> OpenImageFileThenReturnBitmapFile ", DateTime.Now, "The operation was not successful  !!!   you have proplem in 0001 methode   !!!  ", ex.Message);
            return null;

        }

