    // تحويل النص الى صورة
    /// <summary>
    /// 07003  >>  convert string to image
    /// </summary>
    /// <param name="text">Required : Base64String we need convert to image / string Variable</param>
    /// <returns></returns>
    public static Image ConvertBase64StringToImage(string text)
    {
        try
        {
            byte[] imgg = Convert.FromBase64String(text);
            MemoryStream ms = new MemoryStream(imgg);
            return Image.FromStream(ms);
        }
        catch (Exception ex)
        {
            GeneralAction.AddNewNotification("Images >> ConvertBase64StringToImage ", DateTime.Now, ex.Message, ex.Message);
            return null;
        }

    }

    // تحويل الصورة الى نص
    /// <summary>
    /// 07004  >>  convert image to string 
    /// </summary>
    /// <param name="img">Required : image we need convert to Base64String / Image Variable</param>
    /// <returns></returns>
    public static string ConvertImageToBase64String(Image image)
    {
        try
        {
            if(image != null)
            {
                using (MemoryStream m = new MemoryStream())
                {
                    image.Save(m, image.RawFormat);
                    byte[] imageBytes = m.ToArray();

                    // Convert byte[] to Base64 String
                    string base64String = Convert.ToBase64String(imageBytes);
                    return base64String;
                }
            }
            else
            {
                return "";
            }
        }
        catch (Exception ex)
        {
            GeneralAction.AddNewNotification("Images >> ConvertImageToBase64String ", DateTime.Now, ex.Message, ex.Message);
            return "";
        }

    }

    // تحويل نص الى ايقونه
    /// <summary>
    /// 07005  >> convert Base64String to icon
    /// </summary>
    /// <param name="Base64String">Required : image as Base64String / string Variable </param>
    /// <returns>Return Icon Variable</returns>
    public static Icon convertBase64StringToIcon(string Base64String)
    {
        try
        {
            Bitmap bitmap = new Bitmap(Images.ConvertBase64StringToImage(Base64String));
            return Icon.FromHandle(bitmap.GetHicon());
        }
        catch (Exception ex)
        {
            GeneralAction.AddNewNotification("Images >> convertBase64StringToIcon ", DateTime.Now, ex.Message, ex.Message);
            return null;
        }

    }

